import React, { useState, useEffect } from 'react';
import { reviewService } from '../services/api';

const ReviewList = ({ restaurantId, refreshTrigger }) => {
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchReviews = async () => {
            try {
                const response = await reviewService.getReviews(restaurantId);
                setReviews(response.data);
            } catch (err) {
                setError('Failed to load reviews');
            } finally {
                setLoading(false);
            }
        };

        fetchReviews();
    }, [restaurantId, refreshTrigger]);

    if (loading) return <div>Loading reviews...</div>;
    if (error) return <div className="text-danger">{error}</div>;
    if (reviews.length === 0) return <div>No reviews yet. Be the first to review!</div>;

    return (
        <div className="review-list mt-4">
            <h3>Reviews</h3>
            {reviews.map((review) => (
                <div key={review._id} className="card mb-3">
                    <div className="card-body">
                        <div className="d-flex justify-content-between">
                            <h5 className="card-title">{review.user ? review.user.name : 'Anonymous'}</h5>
                            <span className="badge bg-warning text-dark">
                                {review.rating} / 5
                            </span>
                        </div>
                        <p className="card-text">{review.comment}</p>
                        <small className="text-muted">
                            {new Date(review.createdAt).toLocaleDateString()}
                        </small>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default ReviewList;
