import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../services/AuthContext';
import '../styles/Navbar.css';

export default function Navbar({ onMenuClick }) {
  const navigate = useNavigate();
  const { isAuthenticated, user, logout } = useAuth();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <button className="menu-toggle" onClick={onMenuClick}>
          <span></span>
          <span></span>
          <span></span>
        </button>

        <div className="navbar-brand" onClick={() => navigate('/')}>
          <span className="brand-icon">üçΩÔ∏è</span>
          <h1>RestaurantHub</h1>
        </div>

        <div className="navbar-menu">
          {isAuthenticated ? (
            <>
              <button className="link-btn" onClick={() => navigate('/restaurants')}>
                <span className="nav-icon">üè™</span> Restaurants
              </button>
              <button className="link-btn" onClick={() => navigate('/bookings')}>
                <span className="nav-icon">üìÖ</span> Bookings
              </button>
              <button className="link-btn" onClick={() => navigate('/orders')}>
                <span className="nav-icon">üõí</span> Orders
              </button>
              
              <div className="navbar-divider"></div>

              <div className="user-section">
                <div className="user-avatar">{user?.name?.charAt(0) || 'U'}</div>
                <span className="user-name">{user?.name}</span>
              </div>
              
              <button onClick={handleLogout} className="btn btn-logout">
                Logout
              </button>
            </>
          ) : (
            <>
              <button className="link-btn" onClick={() => navigate('/login')}>Login</button>
              <button className="link-btn" onClick={() => navigate('/register')}>Register</button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}
